SELECT a.id, a.resource_name, a.resource_url, a.description, a.resource_icon, a.pid, CASE a.resource_status WHEN 1 THEN '有效' when 2 then '无效' end as status_name, CASE a.resource_type WHEN 1 THEN '一级菜单' when 2 then '二级菜单' when 3 then '按钮' end as type_name, a.create_time, a.update_time, b.resource_name as pid_name FROM system_resource a LEFT JOIN system_resource b ON a.pid = b.id
a.resource_name like concat('%',#{resourceName},'%')
a.pid = #{pid}
LIMIT #{startOfPage}, #{pageSize}

select
count(1)
from system_resource


    resource_name = #{resourceName}


    pid = #{pid}

